import React from 'react';
import './Hero.css';

function Hero() {
  return (
    <section className="hero">
      <div className="hero-content">
        <h1>Empowering Businesses to Reclaim Their Heritage</h1>
        <p>Fast, flexible funding solutions tailored for small business success.</p>
        <a href="#contact" className="cta-button">Get Funded Today</a>
      </div>
    </section>
  );
}

export default Hero;
