import React from 'react';
import '../styles/About.css';


function About() {
  return (
    <section className="about" id="about">
      <h2>About Us</h2>
      <p>
        The Heritage Recovery is committed to supporting entrepreneurs and small business owners with the capital they need to grow, thrive, and reclaim their financial strength.
      </p>
    </section>
  );
}

export default About;
